CREATE TABLE clientes (id INTEGER PRIMARY KEY,nome TEXT,cidade TEXT);CREATE TABLE pedidos (id INTEGER PRIMARY KEY,cliente_id INTEGER,valor REAL,data DATE,FOREIGN KEY(cliente_id) REFERENCES clientes(id));INSERT INTO clientes VALUES(1, 'Ana', 'São Paulo'),(2, 'Bruno', 'Rio de Janeiro'),(3, 'Carlos', 'Belo Horizonte'),(4, 'Daniela', 'São Paulo');INSERT INTO pedidos VALUES(1, 1, 150.50, '2024-01-10'),(2, 1, 299.99, '2024-02-05'),(3, 2, 75.00,  '2024-02-20'),(4, 3, 623.00, '2024-03-01'),(5, 4, 120.00, '2024-03-15'),(6, 4, 980.00, '2024-04-02');